package vn.edu.iuh.fit.lab_week_2_nguyenchicuong.controllers;import jakarta.servlet.ServletException;import jakarta.servlet.annotation.WebServlet;import jakarta.servlet.http.HttpServlet;import jakarta.servlet.http.HttpServletRequest;import jakarta.servlet.http.HttpServletResponse;import vn.edu.iuh.fit.lab_week_2_nguyenchicuong.models.Product;import vn.edu.iuh.fit.lab_week_2_nguyenchicuong.reposities.ProductRepository;import java.io.IOException;import java.util.List;@WebServlet(name = "ProductController",value = "/product")public class ProductSeverlet extends HttpServlet {    private ProductRepository productRepository=new ProductRepository(Product.class);    @Override    protected void doGet(HttpServletRequest req, HttpServletResponse resp) throws ServletException, IOException {        List<Product> listProduct = productRepository.getAll();        System.out.println(listProduct);        req.setAttribute("productList",listProduct);        req.getRequestDispatcher("index.jsp").forward(req,resp);    }    @Override    protected void doPost(HttpServletRequest req, HttpServletResponse resp) throws ServletException, IOException {        super.doPost(req, resp);    }}